import asyncio
import threading
import os
import edge_tts
import pygame
from Features.print_with_time import print_time

# VOICE = "hi-IN-MadhurNeural"
VOICE = "hi-IN-SwaraNeural"
BUFFER_SIZE = 1024


def remove_file(file_path):
    max_attempts = 3
    attempts = 0
    while attempts < max_attempts:
        try:
            with open(file_path, "wb"):
                pass
            os.remove(file_path)
            break
        except Exception as e:
            print(f"error : {e}")
            attempts += 1


async def amain(TEXT, output_file) -> None:
    try:
        cm_txt = edge_tts.Communicate(TEXT, VOICE)
        await cm_txt.save(output_file)
        thread = threading.Thread(target=play_audio, args=(output_file,))
        thread.start()
        thread.join()
    except Exception as e:
        print(f"error : {e}")

    finally:
        remove_file(output_file)


def play_audio(file_path):
    try:
        pygame.init()
        pygame.mixer.init()
        sound = pygame.mixer.Sound(file_path)
        sound.play()

        while pygame.mixer.get_busy():
            pygame.time.Clock().tick(10)

    except Exception as e:
        print(f"error : {e}")


def speak(TEXT, output_file=None):
    if output_file is None:
        output_file = f"{os.getcwd()}/speech.mp3"
    asyncio.run(amain(TEXT, output_file))


def Speak(TEXT, output_file=None, PRINT=True):
    speech_thread = threading.Thread(target=speak, args=(TEXT, output_file))
    speech_thread.start()

    if PRINT:
        print_thread = threading.Thread(target=print_time, args=(TEXT,))
        print_thread.start()

    speech_thread.join()  # Wait for the speech thread to finish
    pygame.quit()  # Quit pygame after all threads have finished

 
